import pandas as pd# Данная функция определяет наличие в данных пустых значений и суммирует# значения для каждого столбцаdef check_missing_values(data):    return data.isnull().sum()# Данная функция создает копию данных и заполняет пропущенные значения в каждом# столбце модой. В случае наличия двух и более мод выбирается первая. В итоге# фукция возвращает копию данных без пропущенных значений.def fill_missing_values(data):    data_copy = data.copy()        for column in data_copy.columns:        data_copy[column].fillna(data_copy[column].mode()[0], inplace = True)    return data_copy# Данная функция создает отчет о пропущенных значения. Указано количество# пропущенных значений в каждом столбце и их процентное соотношение.def missing_values_report(data):    missing_values = check_missing_values(data)    total_lines = len(data)    percent = (missing_values / total_lines) * 100    return pd.DataFrame({"Number of missing values": missing_values, "Percent of missing values": percent})    