import matplotlib.pyplot as pltimport seaborn as sns# Данная функция создает гистограммуdef plot_histogram(data, column):    if column in data.columns:        data[column].plot(kind = 'hist', bins = 20, title = f'Histogram of {column}')        plt.xlabel(column)        plt.show()# Данная функция строит линейный графикdef plot_line(data, x, y):    if x in data.columns and y in data.columns:        plt.plot(data[x], data[y], marker = "o")        plt.title(f'Line Plot for {x} and {y}')        plt.xlabel(x)        plt.ylabel(y)        plt.show()# Данная функция строит диаграму рассеянияdef plot_scatter(data, x, y):    if x in data.columns and y in data.columns:        sns.scatterplot(data = data, x = x, y = y)        plt.title(f'Scatter Plot of {x} and {y}')        plt.xlabel(x)        plt.ylabel(y)        plt.show()